mapper.select:
SELECT ANIUSERNAME, ANISAVENAME, ANIFILESIZE 
FROM ANIATTACH
WHERE ANINO = #{aniNo}

mapper.update:
UPDATE ANI
SET ANIBREED = #{aniBreed}, ANINAME = #{aniName}, ANIBIRTH = #{aniBirth}, ANISITE = #{aniSite}, ANIINFO = #{aniInfo}, 
	SPECIESNO = #{speciesNo}, ANIGENDER = #{aniGender}, NEUTER = #{neuter}, MEMBERNO = #{memberNo}
WHERE ANINO = #{aniNo}

UPDATE ANI
SET ANIDEL ='1'
WHERE ANINO = #{aniNo}
