mapper.select:
SELECT MYPAGENO, MYPAGEDEL, MEMBERNO, MYPAGECONTENT, MYPAGEDATE, MYPAGETITLE
FROM MYPAGE
WHERE MYPAGENO = #{mypageNo}

mapper.update:
UPDATE MYPAGE 
SET MYPAGETITLE = #{mypageTitle}, MYPAGECONTENT = #{mypageContent}
WHERE MYPAGENO = #{mypageNo}

UPDATE MYPAGE  
SET MYPAGEDEL = 0
WHERE MYPAGENO = #{ mypageNo }
